import React from 'react';

const ProductItem = ({ product }) => {

    return (
        <div>
            <h4>{product.name}</h4>
            <div className="crop">
            <img style={{ width: 200 }} src={product.imgUrl} alt="" />

            </div>
            <p>{product.description}</p>
            <h6>{product.price}</h6>
        </div>
    );
}

export default ProductItem;
